import { Button, ButtonToolbar, Form, FormControl } from "react-bootstrap";
import SideBar from "../components/SideBar";
import FeatureProduct from "../landing/FeatureProduct";

export default function ViewInventory() {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = () => {
    // Call the onSearch callback with the search term
    onSearch(searchTerm);
  };
  const FilterBar = () => {
    return (
      <ButtonToolbar className="justify-content-between">
        <div>
          {" "}
          <Button
            variant="outline-primary"
            style={{ marginRight: 10 }}
            size="lg"
          >
            Seafood
          </Button>
          <Button
            variant="outline-primary"
            style={{ marginRight: 10 }}
            size="lg"
          >
            Veggie
          </Button>
          <Button
            variant="outline-primary"
            style={{ marginRight: 10 }}
            size="lg"
          >
            Sauce
          </Button>
        </div>

        <div>
          <Form inline>
            <FormControl
              type="text"
              placeholder="Search"
              className="mr-sm-2"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Button variant="outline-success" onClick={handleSearch}>Search</Button>
          </Form></div>

      </ButtonToolbar>
    );
  };
  return (
    <div className="container mt-5 py-4 px-xl-5">
      <div className="row mb-4 mt-lg-3">
        <div className="d-none d-lg-block col-lg-3">
          <div className="border rounded shadow-sm">
            <SideBar />
          </div>
        </div>
        <div className="col-lg-9">
          <div className="d-flex flex-column h-100">
            <div className="row mb-3">View Inventory</div>
            {/* todo: filter bar */}

            <FilterBar></FilterBar>
            <br />

            <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
              {Array.from({ length: 9 }, (_, i) => {
                return <FeatureProduct key={i} />;
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
